        madd    x16, x15, x10, x6
        madd    x17, x15, x11, x7
        csel    x6, x4, xzr, ne
        ccmp    x1, xzr, #0x8, ne
        cneg    x1, x1, ge
        cneg    x6, x6, ge
        csel    x4, x5, x4, ge
        add     x5, x5, x6
        add     x1, x1, #0x2
        tst     x5, #0x2
        asr     x5, x5, #1
        csel    x6, x4, xzr, ne
        ccmp    x1, xzr, #0x8, ne
        cneg    x1, x1, ge
        cneg    x6, x6, ge
        csel    x4, x5, x4, ge
        add     x5, x5, x6
        add     x1, x1, #0x2
        tst     x5, #0x2
        asr     x5, x5, #1
        csel    x6, x4, xzr, ne
        ccmp    x1, xzr, #0x8, ne
        cneg    x1, x1, ge
        cneg    x6, x6, ge
        csel    x4, x5, x4, ge
        add     x5, x5, x6
        add     x1, x1, #0x2
        tst     x5, #0x2
        asr     x5, x5, #1
        csel    x6, x4, xzr, ne
        ccmp    x1, xzr, #0x8, ne
        cneg    x1, x1, ge
        cneg    x6, x6, ge
        csel    x4, x5, x4, ge
        add     x5, x5, x6
        add     x1, x1, #0x2
        tst     x5, #0x2
        asr     x5, x5, #1
        csel    x6, x4, xzr, ne
        ccmp    x1, xzr, #0x8, ne
        cneg    x1, x1, ge
        cneg    x6, x6, ge
        csel    x4, x5, x4, ge
        add     x5, x5, x6
        add     x1, x1, #0x2
        tst     x5, #0x2
        asr     x5, x5, #1
        csel    x6, x4, xzr, ne
        ccmp    x1, xzr, #0x8, ne
        cneg    x1, x1, ge
        cneg    x6, x6, ge
        csel    x4, x5, x4, ge
        add     x5, x5, x6
        add     x1, x1, #0x2
        tst     x5, #0x2
        asr     x5, x5, #1
        csel    x6, x4, xzr, ne
        ccmp    x1, xzr, #0x8, ne
        cneg    x1, x1, ge
        cneg    x6, x6, ge
        csel    x4, x5, x4, ge
        add     x5, x5, x6
        add     x1, x1, #0x2
        tst     x5, #0x2
        asr     x5, x5, #1
        csel    x6, x4, xzr, ne
        ccmp    x1, xzr, #0x8, ne
        cneg    x1, x1, ge
        cneg    x6, x6, ge
        csel    x4, x5, x4, ge
        add     x5, x5, x6
        add     x1, x1, #0x2
        tst     x5, #0x2
        asr     x5, x5, #1
        csel    x6, x4, xzr, ne
        ccmp    x1, xzr, #0x8, ne
        cneg    x1, x1, ge
        cneg    x6, x6, ge
        csel    x4, x5, x4, ge
        add     x5, x5, x6
        add     x1, x1, #0x2
        asr     x5, x5, #1
        add     x12, x4, #0x100, lsl #12
        sbfx    x12, x12, #22, #21
        mov     x15, #0x100000
        add     x15, x15, x15, lsl #21
        add     x13, x4, x15
        asr     x13, x13, #43
        add     x14, x5, #0x100, lsl #12
        sbfx    x14, x14, #22, #21
        add     x15, x5, x15
        asr     x15, x15, #43
        mneg    x2, x12, x8
        mneg    x3, x12, x9
        mneg    x4, x14, x8
        mneg    x5, x14, x9
        msub    x10, x13, x16, x2
        msub    x11, x13, x17, x3
        msub    x12, x15, x16, x4
        msub    x13, x15, x17, x5
        mov     x22, x1
        loop_body_end:

        subs    i, i, #1
        bne     loop

// The 10th and last iteration does not need anything except the
// u value and the sign of f; the latter can be obtained from the
// lowest word of f. So it's done differently from the main loop.
// Find the sign of the new f. For this we just need one digit
// since we know (for in-scope cases) that f is either +1 or -1.
// We don't explicitly shift right by 59 either, but looking at
// bit 63 (or any bit >= 60) of the unshifted result is enough
// to distiguish -1 from +1; this is then made into a mask.

        //ldr     x0, [sp, #0] // f
        ldrh    x0, sp, #0, t00 // f
        //ldr     x1, [sp, #(4*8)] // g
        ldrh    x1, sp, #(4*8), t10 // g
        mul     x0, x0, m00
        madd    x1, x1, m01, x0
        asr     x0, x1, #63

// Now separate out the matrix into sign-magnitude pairs
// and adjust each one based on the sign of f.
//
// Note that at this point we expect |f|=1 and we got its
// sign above, so then since [f,0] == x * [u,v] (mod p_25519)
// we want to flip the sign of u according to that of f.

        cmp     m00, xzr
        csetm   F00, mi
        cneg    m00, m00, mi
        eor     F00, F00, x0

        cmp     m01, xzr
        csetm   F01, mi
        cneg    m01, m01, mi
        eor     F01, F01, x0

        cmp     m10, xzr
        csetm   F10, mi
        cneg    m10, m10, mi
        eor     F10, F10, x0

        cmp     m11, xzr
        csetm   F11, mi
        cneg    m11, m11, mi
        eor     F11, F11, x0

// Adjust the initial value to allow for complement instead of negation

        and     x0, m00, F00
        and     x1, m01, F01
        add     car0, x0, x1

// Digit 0 of [sp, #(8*8)] // u

        //ldr     x7, [sp, #(8*8)] // u
        ldrh    x7, sp, #(8*8), t20 // u
        eor     x1, x7, F00
        mul     x0, x1, m00
        umulh   x1, x1, m00
        adds    x4, car0, x0
        adc     x2, xzr, x1
        //ldr     x8, [sp, #(12*8)] // v
        ldrh    x8, sp, #(12*8), v30 // v
        eor     x1, x8, F01
        mul     x0, x1, m01
        umulh   x1, x1, m01
        adds    x4, x4, x0
        //str     x4, [sp, #(8*8)] // u
        strh    x4, sp, #(8*8), t20 // u
        adc     x2, x2, x1

// Digit 1 of [sp, #(8*8)] // u

        //ldr     x7, [sp, #(8*8)+8] // u +8
        ldrh    x7, sp, #(8*8)+8, t21 // u +8
        eor     x1, x7, F00
        mul     x0, x1, m00
        umulh   x1, x1, m00
        adds    x2, x2, x0
        adc     x6, xzr, x1
        //ldr     x8, [sp, #(12*8)+8] // v +8
        ldrh    x8, sp, #(12*8)+8, t31 // v +8
        eor     x1, x8, F01
        mul     x0, x1, m01
        umulh   x1, x1, m01
        adds    x2, x2, x0
        //str     x2, [sp, #(8*8)+8] // u +8
        strh    x2, sp, #(8*8)+8, t21 // u +8
        adc     x6, x6, x1

// Digit 2 of [sp, #(8*8)] // u

        //ldr     x7, [sp, #(8*8)+2*8] // u +2*8
        ldrh    x7, sp, #(8*8)+2*8, t22 // u +2*8
        eor     x1, x7, F00
        mul     x0, x1, m00
        umulh   x1, x1, m00
        adds    x6, x6, x0
        adc     x5, xzr, x1
        //ldr     x8, [sp, #(12*8)+2*8] // v +2*8
        ldrh    x8, sp, #(12*8)+2*8, t32 // v +2*8
        eor     x1, x8, F01
        mul     x0, x1, m01
        umulh   x1, x1, m01
        adds    x6, x6, x0
        //str     x6, [sp, #(8*8)+2*8] // u +2*8
        strh    x6, sp, #(8*8)+2*8, t22 // u +2*8
        adc     x5, x5, x1

// Digits 3 and 4 of u (top is unsigned)

        //ldr     x7, [sp, #(8*8)+3*8] // u +3*8
        ldrh    x7, sp, #(8*8)+3*8, t23 // u +3*8
        eor     x1, x7, F00
        and     x3, F00, m00
        neg     x3, x3
        mul     x0, x1, m00
        umulh   x1, x1, m00
        adds    x5, x5, x0
        adc     x3, x3, x1
        //ldr     x8, [sp, #(12*8)+3*8] // v +3*8
        ldrh    x8, sp, #(12*8)+3*8, t33 // v +3*8
        eor     x1, x8, F01
        and     x0, F01, m01
        sub     x3, x3, x0
        mul     x0, x1, m01
        umulh   x1, x1, m01
        adds    x5, x5, x0
        adc     x3, x3, x1

// Modular reduction of u, this time strictly 2^255-19.

        extr    x6, x3, x5, #63
        //ldp     x0, x1, [sp, #(8*8)] // u
        ldphp   x0, x1, sp, #(8*8), t20, t21 // u
        tst     x3, x3
        cinc    x6, x6, pl
        mov     x3, #19
        mul     x4, x6, x3
        add     x5, x5, x6, lsl #63
        smulh   x6, x6, x3
        //ldr     x2, [sp, #(8*8)+2*8] // u +2*8
        ldrh    x2, sp, #(8*8)+2*8, t22 // u +2*8
        adds    x0, x0, x4
        adcs    x1, x1, x6
        asr     x6, x6, #63
        adcs    x2, x2, x6
        adcs    x5, x5, x6
        csel    x3, x3, xzr, mi
        subs    x0, x0, x3
        sbcs    x1, x1, xzr
        sbcs    x2, x2, xzr
        sbc     x5, x5, xzr
        and     x5, x5, #0x7fffffffffffffff

// Store it back to the final output

        ldr     x4, [sp, NSPACE]
        stp     x0, x1, [x4]
        stp     x2, x5, [x4, #16]

// Restore stack and registers

        add     sp, sp, NSPACE
        add     sp, sp, 16

        ldp     x19, x20, [sp, #(5*16)]
        ldp     x21, x22, [sp, #(4*16)]
        ldp     x23, x24, [sp, #(3*16)]
        ldp     x25, x26, [sp, #(2*16)]
        ldp     x27, x28, [sp, #(1*16)]
        ldp     x29, x30, [sp, #(0*16)]
        add     sp, sp, #(6*16)
        ret

